mapper.select:
select main.prj_id, main.doc_id, main.rev_id, main.folder_id, main.doc_no, main.doc_type ,main.rev_code_id, main.rev_no ,main.sfile_nm,main.rfile_nm, main.file_type, main.file_size, main.title, main.del_yn, main.unread_yn, main.lock_yn, main.trans_ref_doc_id, main.reg_id, main.reg_date, main.mod_id, main.mod_date ,CASE WHEN main.rev_id = main.max_rev_id THEN 'NEW' ELSE 'OLD' END status from ( select a.prj_id, a.doc_id, a.rev_id, a.folder_id, a.doc_no, a.doc_type ,a.rev_code_id, (select code.set_code from p_prj_code_settings code where code.prj_id = a.prj_id and code.set_code_type = 'REVISION NUMBER' and code.set_code_id = a.rev_code_id) as rev_no ,a.sfile_nm,a.rfile_nm, a.file_type, a.file_size, a.title, a.del_yn, a.unread_yn, a.lock_yn, a.trans_ref_doc_id, a.reg_id, a.reg_date, a.mod_id, a.mod_date ,(select max(s1.rev_id) as rev_id from p_prj_document_index s1 where s1.del_yn!='Y' and s1.prj_id = a.prj_id and s1.folder_id = a.folder_id and s1.doc_id = a.doc_id )max_rev_id from p_prj_document_index a where a.prj_id = #{prj_id} and a.folder_id = #{folder_id} and del_yn!='Y'
and a.unread_yn!='N'
)main order by main.doc_id, main.rev_id, main.doc_no

    	select * from p_prj_docno_chg_hist 
    	where 1=1
		
    		and prj_id=#{prj_id}
		
		
			and substring(log_date,1,8) between #{from} and #{to}
		
		order by log_date desc

