mapper.select:
select a.doc_no, (select doc_no from p_prj_document_index where prj_id=#{prj_id} and doc_id=#{doc_id} and rev_id=(select FORMAT(MAX(CONVERT(INT,rev_id))-1,'000') from p_prj_document_index where prj_id=#{prj_id} and doc_id=#{doc_id})) as old_doc_no, a.doc_type, (select code.set_code from p_prj_code_settings code where code.prj_id = a.prj_id and code.set_code_type = 'REVISION NUMBER' and code.set_code_id = a.rev_code_id) as rev_no, a.rev_code_id, a.sfile_nm, a.rfile_nm, a.file_type, a.file_size, a.attach_file_date, a.title, a.lock_yn, (select users.user_eng_nm from p_users users where user_id=a.reg_id) as reg_nm, (select users.user_eng_nm from p_users users where user_id=a.mod_id) as mod_nm, (select users.user_kor_nm from p_users users where user_id=a.reg_id) as reg_kor_nm, (select users.user_kor_nm from p_users users where user_id=a.mod_id) as mod_kor_nm, a.reg_id, a.reg_date, a.mod_id, a.mod_date, (select discip_code from p_prj_discipline_info where prj_id=#{prj_id} and discip_code_id=b.discip_code_id) as discip_code, b.discip_code_id, b.designer_id, (select user_kor_nm from p_users where user_id=b.designer_id) as designer_nm, b.prfe_designer_id, (select user_kor_nm from p_users where user_id=b.prfe_designer_id) as pre_designer_nm, b.wbs_code_id, (select wbs_code from p_prj_wbs_code where prj_id=#{prj_id} and wbs_code_id=b.wbs_code_id) as wbs_code, (select wbs_desc from p_prj_wbs_code where prj_id=#{prj_id} and wbs_code_id=b.wbs_code_id) as wbs_desc, (select wgtval from p_prj_wbs_code where prj_id=#{prj_id} and wbs_code_id=b.wbs_code_id) as wgtval, (select discip_code from p_prj_discipline_info where prj_id=#{prj_id} and discip_code_id=b.discip_code_id) as discipline, b.remark, b.po_doc_no, b.po_doc_title, b.vendor_nm, b.design_part, (select set_code from p_prj_code_settings where prj_id=#{prj_id} and set_code_type='VENDOR DOC.CATEGORY' and set_code_id=b.category_cd_id) as category_code, (select set_desc from p_prj_code_settings where prj_id=#{prj_id} and set_code_type='VENDOR DOC.CATEGORY' and set_code_id=b.category_cd_id) as category_display, b.category_cd_id, (select set_code from p_prj_code_settings where prj_id=#{prj_id} and set_code_type='VENDOR DOC.SIZE' and set_code_id=b.size_code_id) as size_code, b.size_code_id, (select set_code from p_prj_code_settings where prj_id=#{prj_id} and set_code_type='DOC.TYPE' and set_code_id=b.doc_type_id) as doc_type_code, b.doc_type_id, (select discip_display from p_prj_discipline_info where prj_id=#{prj_id} and discip_code_id=b.discip_code_id) as discip_display, (select max(rev_id) from p_prj_document_index where prj_id = #{prj_id} and doc_id=#{doc_id} and del_yn!='Y') as rev_max, a.doc_id, a.rev_id, a.folder_id,
(select top 1 tf.rev_no from p_prj_vtr_file tf where tf.prj_id=a.prj_id and tf.doc_id=a.doc_id and tf.rev_id!=a.rev_id order by tr_id desc) as p_rev
from p_prj_document_index a left outer join p_prj_vdr_info b on a.prj_id=b.prj_id and a.doc_id=b.doc_id and a.rev_id=b.rev_id where a.prj_id=#{prj_id} and a.doc_id=#{doc_id} and a.rev_id=#{rev_id}

